import React from "react";
import SearchBar from "../Header/SearchBar";
import CategoryFilter from "../Header/CategoryFilter";
import useTheme from "../../hooks/useTheme";

export default function Topbar({ query, onSearch, categories, category, setCategory, onAdd, onExport }) {
  const [theme, setTheme] = useTheme();

  return (
    <header className="bg-white dark:bg-gray-800 border-b relative z-20">
      <div className="max-w-7xl mx-auto px-4 py-4 flex items-center justify-between">
        <div className="flex items-center gap-4">
          <SearchBar value={query} onSearch={onSearch} />
          <CategoryFilter categories={categories} value={category} onChange={setCategory} />
        </div>

        <div className="flex items-center gap-3">
          <button onClick={onExport} className="px-3 py-2 bg-green-600 text-white rounded-md hover:bg-green-700">
            Export CSV
          </button>

          <button onClick={onAdd} className="px-3 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700">
            Add Product
          </button>

          {/* Dark mode toggle */}
          <button
            onClick={() => setTheme(theme === "dark" ? "light" : "dark")}
            className="px-3 py-2 border rounded-md hover:bg-gray-200 dark:hover:bg-gray-700 transition"
          >
            {theme === "dark" ? "‚òÄÔ∏è" : "üåô"}
          </button>
        </div>
      </div>
    </header>
  );
}
