import React, { useEffect, useState } from "react";
import axios from "./api/axiosClient";
import Sidebar from "./components/Layout/Sidebar";
import Topbar from "./components/Layout/Topbar";
import ProductTable from "./components/Table/ProductTable";
import HistorySidebar from "./components/Sidebar/HistorySidebar";
import AddProductModal from "./components/Modals/AddProductModal";
import useTheme from "./hooks/useTheme";

export default function App() {
  const [theme] = useTheme(); // run theme init

  const [products, setProducts] = useState([]);
  const [query, setQuery] = useState("");
  const [category, setCategory] = useState("");
  const [selected, setSelected] = useState(null);
  const [showAddModal, setShowAddModal] = useState(false);

  const fetchAll = async () => {
    try {
      const res = await axios.get("/api/products");
      setProducts(res.data);
    } catch (err) {
      console.error("Error fetching products:", err);
    }
  };

  useEffect(() => {
    fetchAll();
  }, []);

  const handleSearch = async (q) => {
    setQuery(q);
    if (!q.trim()) return fetchAll();
    try {
      const res = await axios.get("/api/products/search", { params: { name: q } });
      setProducts(res.data);
    } catch (err) {
      console.error("Search error:", err);
    }
  };

  const categories = Array.from(new Set(products.map((p) => p.category).filter(Boolean)));

  return (
    <div className="min-h-screen flex bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-gray-100 relative">

      {/* Sidebar */}
      <div className="z-30">
        <Sidebar />
      </div>

      {/* Main content area */}
      <div className="flex-1 flex flex-col relative z-10">

        {/* Topbar */}
        <div className="relative z-20 shadow-sm">
          <Topbar
            query={query}
            onSearch={handleSearch}
            categories={categories}
            category={category}
            setCategory={setCategory}
            onAdd={() => setShowAddModal(true)}
            onExport={async () => {
              const res = await axios.get("/api/products/export", { responseType: "blob" });
              const url = window.URL.createObjectURL(new Blob([res.data]));
              const link = document.createElement("a");
              link.href = url;
              link.setAttribute("download", "products.csv");
              document.body.appendChild(link);
              link.click();
              link.remove();
            }}
          />
        </div>

        {/* Content area */}
        <main className="p-6 max-w-7xl mx-auto relative z-10">
          <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">

            {/* Table */}
            <div className="lg:col-span-3 bg-white dark:bg-gray-800 rounded-lg shadow p-4 relative z-10">
              <ProductTable
                products={category ? products.filter((p) => p.category === category) : products}
                onUpdate={fetchAll}
                onSelect={setSelected}
              />
            </div>

            {/* History */}
            <div className="lg:col-span-1 relative z-10">
              <HistorySidebar product={selected} onClose={() => setSelected(null)} />
            </div>

          </div>
        </main>

      </div>

      {/* Modal */}
      {showAddModal && (
        <div className="z-40">
          <AddProductModal onClose={() => setShowAddModal(false)} onCreated={fetchAll} />
        </div>
      )}
    </div>
  );
}
