import React, {useEffect, useState} from 'react';
import axios from './api/axiosClient';
import ProductTable from './components/Table/ProductTable';
import SearchBar from './components/Header/SearchBar';
import CategoryFilter from './components/Header/CategoryFilter';
import HistorySidebar from './components/Sidebar/HistorySidebar';

function App(){
  const [showAddModal, setShowAddModal] = useState(false);

  const [products, setProducts] = useState([]);
  const [query, setQuery] = useState('');
  const [category, setCategory] = useState('');
  const [selected, setSelected] = useState(null);

  const fetchAll = async () => {
    const res = await axios.get('/api/products');
    setProducts(res.data);
  };

  useEffect(()=>{ fetchAll(); }, []);

  const handleSearch = async (q) => {
    setQuery(q);
    if (!q) return fetchAll();
    const res = await axios.get('/api/products/search', { params: { name: q } });
    setProducts(res.data);
  };

  const categories = Array.from(new Set(products.map(p=>p.category).filter(Boolean)));

  return (
    <div style={{padding:20, fontFamily:'Arial, sans-serif'}}>
      <header style={{display:'flex', justifyContent:'space-between', alignItems:'center', marginBottom:20}}>
        <div style={{display:'flex', gap:10, alignItems:'center'}}>
          <SearchBar value={query} onSearch={handleSearch}/>
          <CategoryFilter categories={categories} value={category} onChange={setCategory}/>
        </div>
        <div>
          <button onClick={async ()=>{ // export
            const res = await axios.get('/api/products/export', { responseType: 'blob' });
            const url = window.URL.createObjectURL(new Blob([res.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download','products.csv');
            document.body.appendChild(link);
            link.click();
            link.remove();
          }}>Export CSV</button>
        </div>
      </header>

      <main style={{display:'flex', gap:20}}>
        <ProductTable products={products.filter(p => category ? p.category === category : true)} onUpdate={fetchAll} onSelect={setSelected}/>
        <HistorySidebar product={selected} onClose={()=>setSelected(null)} />
      </main>
    </div>
  );
}

export default App;
