import React, { createContext, useContext, useState, useCallback } from "react";

const ToastContext = createContext();

let idCounter = 1;

export function useToast() {
  return useContext(ToastContext);
}

export default function ToastProvider({ children }) {
  const [toasts, setToasts] = useState([]);

  const push = useCallback((type, message, ttl = 4000) => {
    const id = idCounter++;
    setToasts(t => [...t, { id, type, message }]);
    if (ttl > 0) {
      setTimeout(() => {
        setToasts(t => t.filter(x => x.id !== id));
      }, ttl);
    }
    return id;
  }, []);

  const remove = useCallback((id) => {
    setToasts(t => t.filter(x => x.id !== id));
  }, []);

  const api = {
    success: (msg, ttl) => push('success', msg, ttl),
    error: (msg, ttl) => push('error', msg, ttl),
    info: (msg, ttl) => push('info', msg, ttl),
    remove
  };

  return (
    <ToastContext.Provider value={api}>
      {children}
      <ToastContainer toasts={toasts} onClose={remove} />
    </ToastContext.Provider>
  );
}

/* ToastContainer and ToastItem below */
function ToastContainer({ toasts, onClose }) {
  return (
    <div className="fixed bottom-6 right-6 z-50 flex flex-col gap-3">
      {toasts.map(t => (
        <ToastItem key={t.id} toast={t} onClose={() => onClose(t.id)} />
      ))}
    </div>
  );
}

function ToastItem({ toast, onClose }) {
  const base = "px-4 py-2 rounded shadow-md flex items-start gap-3 max-w-sm";
  const common = "text-sm";
  let cls = "bg-white text-gray-900";
  if (toast.type === 'success') cls = "bg-green-50 border-l-4 border-green-500 text-green-800";
  if (toast.type === 'error') cls = "bg-red-50 border-l-4 border-red-500 text-red-800";
  if (toast.type === 'info') cls = "bg-blue-50 border-l-4 border-blue-500 text-blue-800";

  return (
    <div className={`${base} ${cls}`}>
      <div className={common}>{toast.message}</div>
      <button onClick={onClose} className="ml-auto text-xs opacity-70">âœ•</button>
    </div>
  );
}
