// frontend/src/components/Header/SearchBar.jsx
import React, { useState, useEffect, useRef } from "react";

export default function SearchBar({ value = "", onSearch = () => {}, placeholder = "Search products..." }) {
  const [q, setQ] = useState(value || "");
  const debounceRef = useRef(null);

  // Keep internal state synced when parent changes value (e.g. clearing)
  useEffect(() => {
    setQ(value || "");
  }, [value]);

  // Optional debounce: call onSearch after user stops typing for 350ms
  useEffect(() => {
    // clear existing timer
    if (debounceRef.current) clearTimeout(debounceRef.current);

    debounceRef.current = setTimeout(() => {
      // Only run search when input length > 0 or empty (to reset)
      onSearch(q.trim());
    }, 350);

    return () => {
      if (debounceRef.current) clearTimeout(debounceRef.current);
    };
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [q]); // note: onSearch intentionally not included to avoid re-subscribing

  // Called when user clicks the search button or presses Enter
  const submitNow = (evt) => {
    if (evt) evt.preventDefault();
    if (debounceRef.current) {
      clearTimeout(debounceRef.current);
      debounceRef.current = null;
    }
    onSearch(q.trim());
  };

  return (
    <form onSubmit={submitNow} className="flex items-center gap-2">
      <input
        type="search"
        value={q}
        onChange={(e) => setQ(e.target.value)}
        placeholder={placeholder}
        aria-label="Search products"
        className="px-3 py-2 rounded border w-64 bg-white dark:bg-gray-700 text-gray-900 dark:text-gray-100"
      />
      <button
        type="submit"
        onClick={submitNow}
        className="px-3 py-2 bg-gray-200 dark:bg-gray-800 rounded"
      >
        Search
      </button>
    </form>
  );
}
