import React, {useEffect, useState} from 'react';
import axios from '../../api/axiosClient';

export default function HistorySidebar({product, onClose}){
  const [logs, setLogs] = useState([]);

  useEffect(()=>{
    if(!product) return setLogs([]);
    (async ()=> {
      const res = await axios.get(`/api/products/${product.id}/history`);
      setLogs(res.data);
    })();
  }, [product]);

  if(!product) return <div style={{width:320}} />;

  return (
    <aside style={{width:320, borderLeft:'1px solid #ddd', padding:10}}>
      <div style={{display:'flex', justifyContent:'space-between'}}>
        <h3>History: {product.name}</h3>
        <button onClick={onClose}>Close</button>
      </div>
      <table style={{width:'100%'}} border="1" cellPadding="6">
        <thead><tr><th>Date</th><th>Old</th><th>New</th><th>User</th></tr></thead>
        <tbody>
          {logs.map(l=>(
            <tr key={l.id}>
              <td>{new Date(l.change_date).toLocaleString()}</td>
              <td>{l.old_quantity}</td>
              <td>{l.new_quantity}</td>
              <td>{l.user_info}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </aside>
  );
}
