import React, { useEffect, useState } from "react";
import axios from "./api/axiosClient";
import ProductTable from "./components/Table/ProductTable";
import SearchBar from "./components/Header/SearchBar";
import CategoryFilter from "./components/Header/CategoryFilter";
import HistorySidebar from "./components/Sidebar/HistorySidebar";
import AddProductModal from "./components/Modals/AddProductModal";

export default function App() {
  const [products, setProducts] = useState([]);
  const [query, setQuery] = useState("");
  const [category, setCategory] = useState("");
  const [selected, setSelected] = useState(null);
  const [showAddModal, setShowAddModal] = useState(false);

  // Fetch all products
  const fetchAll = async () => {
    try {
      const res = await axios.get("/api/products");
      setProducts(res.data);
    } catch (err) {
      console.error("Error fetching products:", err);
    }
  };

  useEffect(() => {
    fetchAll();
  }, []);

  // Handle search
  const handleSearch = async (q) => {
    setQuery(q);

    if (!q.trim()) {
      return fetchAll();
    }

    try {
      const res = await axios.get("/api/products/search", {
        params: { name: q }
      });
      setProducts(res.data);
    } catch (err) {
      console.error("Search error:", err);
    }
  };

  // Extract unique categories
  const categories = Array.from(
    new Set(products.map((p) => p.category).filter(Boolean))
  );

  return (
    <div style={{ padding: 20, fontFamily: "Arial" }}>
      {/* ---------------- HEADER ---------------- */}
      <header
        style={{
          display: "flex",
          justifyContent: "space-between",
          marginBottom: 20,
        }}
      >
        <div style={{ display: "flex", gap: 10 }}>
          <SearchBar value={query} onSearch={handleSearch} />
          <CategoryFilter
            categories={categories}
            value={category}
            onChange={setCategory}
          />

          {/* ‚≠ê Add Product Button */}
          <button onClick={() => setShowAddModal(true)}>Add Product</button>
        </div>

        {/* Export CSV */}
        <button
          onClick={async () => {
            const res = await axios.get("/api/products/export", {
              responseType: "blob",
            });
            const url = window.URL.createObjectURL(new Blob([res.data]));
            const link = document.createElement("a");
            link.href = url;
            link.setAttribute("download", "products.csv");
            document.body.appendChild(link);
            link.click();
            link.remove();
          }}
        >
          Export CSV
        </button>
      </header>

      {/* ---------------- MAIN CONTENT ---------------- */}
      <main style={{ display: "flex", gap: 20 }}>
        <ProductTable
          products={
            category
              ? products.filter((p) => p.category === category)
              : products
          }
          onUpdate={fetchAll}
          onSelect={setSelected}
        />

        <HistorySidebar product={selected} onClose={() => setSelected(null)} />
      </main>

      {/* ---------------- ADD PRODUCT MODAL ---------------- */}
      {showAddModal && (
        <AddProductModal
          onClose={() => setShowAddModal(false)}
          onCreated={fetchAll}
        />
      )}
    </div>
  );
}
