import React from "react";
import SearchBar from "../Header/SearchBar";
import CategoryFilter from "../Header/CategoryFilter";
import useTheme from "../../hooks/useTheme";

export default function Topbar({
  query,
  onSearch,
  categories,
  category,
  setCategory,
  onAdd,
  onExport
}) {

  return (
    <header
      className="
        border-b border-gray-200 dark:border-gray-700
        bg-gray-50 dark:bg-[rgba(30,33,40,0.75)]
        backdrop-blur-md
        shadow-sm
        sticky top-0 z-20
      "
    >
      <div className="max-w-7xl mx-auto px-6 py-4 flex items-center justify-between">

        {/* Left Section */}
        <div className="flex items-center gap-4">
          <SearchBar value={query} onSearch={onSearch} />
          <CategoryFilter
            categories={categories}
            value={category}
            onChange={setCategory}
          />
        </div>

        {/* Right Section */}
        <div className="flex items-center gap-3">
          <button
            onClick={onExport}
            className="px-4 py-2 bg-green-600 hover:bg-green-700 text-white rounded-md shadow-md"
          >
            Export CSV
          </button>

          <button
            onClick={onAdd}
            className="px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-md shadow-md"
          >
            Add Product
          </button>
        </div>

      </div>
    </header>
  );
}
