import React from "react";
import SearchBar from "../Header/SearchBar";
import CategoryFilter from "../Header/CategoryFilter";

export default function Topbar({ query, onSearch, categories, category, setCategory, onAdd, onExport, theme, setTheme }) {
  const toggleTheme = () => {
    setTheme(theme === "dark" ? "light" : "dark");
  };

  return (
    <header className="bg-white dark:bg-gray-800 border-b">
      <div className="max-w-7xl mx-auto px-4 py-4 flex items-center justify-between">
        <div className="flex items-center gap-4">
          <SearchBar value={query} onSearch={onSearch} />
          <CategoryFilter categories={categories} value={category} onChange={setCategory} />
        </div>

        <div className="flex items-center gap-3">
          <button onClick={onExport} className="px-3 py-2 bg-green-600 text-white rounded-md">Export CSV</button>
          <button onClick={onAdd} className="px-3 py-2 bg-blue-600 text-white rounded-md">Add Product</button>

          {/* dark mode toggle */}
          <button
            onClick={toggleTheme}
            className="ml-3 p-2 rounded-md bg-gray-100 dark:bg-gray-700"
            title="Toggle theme"
          >
            {theme === "dark" ? "üåô" : "‚òÄÔ∏è"}
          </button>
        </div>
      </div>
    </header>
  );
}
