import React, {useState} from 'react';
import axios from '../../api/axiosClient';

export default function ProductRow({product, onUpdate, onSelect}){
  const [isEditing, setEditing] = useState(false);
  const [form, setForm] = useState({...product});

  const save = async () => {
    await axios.put('/api/products/'+product.id, form);
    setEditing(false);
    onUpdate();
  };

  return (
    <tr>
      <td style={{width:60}}><img src={product.image||''} alt="" style={{width:40}}/></td>
      <td>{isEditing ? <input value={form.name} onChange={e=>setForm({...form,name:e.target.value})}/> : product.name}</td>
      <td>{isEditing ? <input value={form.unit} onChange={e=>setForm({...form,unit:e.target.value})}/> : product.unit}</td>
      <td>{isEditing ? <input value={form.category} onChange={e=>setForm({...form,category:e.target.value})}/> : product.category}</td>
      <td>{isEditing ? <input value={form.brand} onChange={e=>setForm({...form,brand:e.target.value})}/> : product.brand}</td>
      <td>{isEditing ? <input type="number" value={form.stock} onChange={e=>setForm({...form,stock:Number(e.target.value)})}/> : product.stock}</td>
      <td>{product.stock>0 ? <span style={{color:'green'}}>In Stock</span> : <span style={{color:'red'}}>Out of Stock</span>}</td>
      <td>
        {isEditing ? <>
          <button onClick={save}>Save</button>
          <button onClick={()=>{ setEditing(false); setForm({...product}); }}>Cancel</button>
        </> : <>
          <button onClick={()=>setEditing(true)}>Edit</button>
          <button onClick={async ()=>{ if(confirm('Delete?')){ await axios.delete('/api/products/'+product.id); onUpdate(); }}}>Delete</button>
          <button onClick={()=>onSelect(product)}>History</button>
        </>}
      </td>
    </tr>
  );
}
